mapper.select:
select notice_key ,notice_id ,(select user_name from user B where A.notice_id=B.user_id) as user_name ,notice_title ,notice_contents ,notice_hit ,notice_wdate ,rnum from ( select notice_key ,notice_id ,notice_title ,notice_contents ,notice_hit ,notice_wdate ,@rownum := @rownum+1 AS rnum -- 새로 일련번호를 붙이자 from notice_board A, (select @rownum:=0) B
where notice_title like concat('%', #{keyword} , '%') or notice_contents like concat('%', #{keyword} , '%')
order by notice_key desc )A limit #{start}, #{pageSize}

mapper.insert:
insert into notice_board (
	notice_id
	, notice_title
	, notice_contents
	, notice_wdate
	, notice_hit
)
values(
	#{notice_id}
	, #{notice_title}
	, #{notice_contents}
	, now()
	, 0
)

mapper.update:
update notice_board 
	set notice_id=#{notice_id}
		,notice_title=#{notice_title}
		,notice_contents=#{notice_contents}
	where notice_key = #{notice_key}
