mapper.insert:
insert into qna_board (
	qna_id
	, qna_title
	, qna_contents
	, qna_wdate
	, qna_hit
)
values(
	#{qna_id}
	, #{qna_title}
	, #{qna_contents}
	, now()
	, 0
)

mapper.update:
update qna_board 
	set qna_id=#{qna_id}
		,qna_title=#{qna_title}
		,qna_contents=#{qna_contents}
	where qna_key = #{qna_key}
