mapper.select:
select count(*) from REVIEW_BOARD
where review_title like concat('%', #{keyword} , '%') or review_contents like concat('%', #{keyword} , '%')
where review_title like concat('%', #{keyword} , '%') or review_contents like concat('%', #{keyword} , '%') and review_category = #{review_category}
where review_title like concat('%', #{keyword} , '%') or review_contents like concat('%', #{keyword} , '%')and review_category = #{review_category}
where review_title like concat('%', #{keyword} , '%') or review_contents like concat('%', #{keyword} , '%')and review_category = #{review_category}
